mapper.select:
select a.id,a.name,a.startDate,a.endDate,u.name as owner
from tbl_activity a join tbl_user u on a.owner=u.id
where a.name like '%' #{aname} '%'
and a.id not in (
    select activityId from tbl_clue_activity_relation where clueId=#{clueId}
    )

select a.id, a.name, n.name as owner, a.startDate, a.endDate from tbl_activity a join tbl_user n on a.owner = n.id
'1'='1'
and a.name like '%' #{name} '%'
and n.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc /* int int 类型 记住 ~~~~！！！！*/ limit #{skipCount},#{pageSize}

select count(*) from tbl_activity a join tbl_user n on a.owner = n.id
'1'='1'
and a.name like '%' #{name} '%'
and n.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}

mapper.update:
update tbl_activity
set
    owner=#{owner}
  ,name=#{name}
  ,startDate=#{startDate}
  ,endDate=#{endDate}
  ,cost=#{cost}
  ,description=#{description}
where id=#{id}
